import React from "react";
import styled from "styled-components";

const NavBtn = styled.button``;

const StyledCompBtn = styled(NavBtn)`
  display: flex;
  align-items: center;
  padding: ${(props) => props.padding};
  box-shadow: ${(props) => props.boxShadow};
  border: 1px solid #2d2a26;
  border-radius: 10px;
  font-weight: bold;
  transition: all 0.3s;
  background-color: ${(props) => props.backgroundParam};
  &:hover {
    box-shadow: 2px 2px ${(props) => props.colorBackBtn};
    transform: translateY(2px);
  }
`;

const ButtonStyledComp = (props) => {
  return <StyledCompBtn {...props} />;
};

export default ButtonStyledComp;
