import React, { Dispatch, SetStateAction } from "react";

import { Button } from "../Button/Button";
import Avatar from "../Images/Avatar.png";
import Close from "../Images/close.png";
import Modal from "../Modal/Modal";

import classes from "./styles.module.css";

interface Props {
  active: boolean;
  setActive: Dispatch<SetStateAction<boolean>>;
  data: string[];
  onClick: () => void;
  showAvatar: boolean;
  transitionOptions: (page: string) => void;
}

const ModalView: React.FC<Props> = ({
  active,
  setActive,
  data,
  onClick,
  showAvatar = false,
  transitionOptions,
}) => {
  return (
    <Modal active={active} setActive={setActive}>
      <div className={classes.elemModal}>
        {data.map((item) => (
          <span
            key={item}
            className={classes.chooseLink}
            onClick={() => transitionOptions(item)}
          >
            {item}
          </span>
        ))}
        {showAvatar && (
          <div className={classes.menuBtn}>
            <Button
              imgContent={Avatar}
              backgroundParam="#FCFCF0"
              paddingParam="16px"
              boxShadowParam="6px 6px #2D2A26"
              colorBackBtn="#2D2A26"
              onClick={onClick}
            ></Button>
          </div>
        )}

        <div className={classes.closeModal} onClick={() => setActive(false)}>
          <img src={Close} alt="" />
        </div>
      </div>
    </Modal>
  );
};

export default ModalView;
