import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

import MenuIcon from "@mui/icons-material/Menu";
import { LinkPage, NamePage } from "./interface";
import ModalView from "./ModalView";
import { Button } from "../Button/Button";
import Avatar from "../Images/Avatar.png";
import Box from "../Images/box.png";

import classes from "./styles.module.css";

const pages = ["About", "How it works", "Blog", "Contacts"];
const settings = ["Profile", "Account", "Dashboard", "Logout"];

const Navbar = () => {
  const [showMenu, setShowMenu] = useState(false);
  const [showParamAcc, setShowParamAcc] = useState(false);

  let navigate = useNavigate();

  const onClick = () => {
    alert("Click");
  };

  const chooseAdresssMenu = (page: any) => {
    if (page === NamePage.About) navigate(LinkPage.About);
    if (page === NamePage["How it works"]) navigate(LinkPage["How it works"]);
    if (page === NamePage.Blog) navigate(LinkPage.Blog);
    if (page === NamePage.Contacts) navigate(LinkPage.Contacts);
  };

  const chooseAdresssSettings = (page: any) => {
    console.log(page, "page");
    // if (page === NamePage.About) navigate(LinkPage.About);
    // if (page === NamePage["How it works"]) navigate(LinkPage["How it works"]);
    // if (page === NamePage.Blog) navigate(LinkPage.Blog);
    // if (page === NamePage.Contacts) navigate(LinkPage.Contacts);
  };

  return (
    <div className={classes.container}>
      <div className={classes.menuBtn} onClick={() => setShowMenu(true)}>
        <MenuIcon />
      </div>
      <div>
        <div className={classes.linkBigScreen}>
          {pages.map((page) => (
            <span
              key={page}
              className={classes.chooseLink}
              onClick={() => chooseAdresssMenu(page)}
            >
              {page}
            </span>
          ))}
        </div>
        <ModalView
          active={showMenu}
          setActive={setShowMenu}
          data={pages}
          onClick={onClick}
          transitionOptions={chooseAdresssMenu}
          showAvatar={true}
        />
        <ModalView
          active={showParamAcc}
          setActive={setShowParamAcc}
          data={settings}
          onClick={onClick}
          showAvatar={false}
          transitionOptions={chooseAdresssSettings}
        />
      </div>
      <div className={classes.groupBtn}>
        <Button
          title="Get a Kit"
          imgContent={Box}
          backgroundParam="#FCFCF0"
          paddingParam="16px 27px"
          boxShadowParam="6px 6px #2D2A26"
          colorBackBtn="#2D2A26"
          paddingForImg="9px"
          onClick={onClick}
        ></Button>
        <div
          className={classes.avatarBtn}
          onClick={() => setShowParamAcc(true)}
        >
          <Button
            imgContent={Avatar}
            backgroundParam="#FCFCF0"
            paddingParam="16px"
            boxShadowParam="6px 6px #2D2A26"
            colorBackBtn="#2D2A26"
          ></Button>
        </div>
      </div>
    </div>
  );
};
export default Navbar;
